mapper.insert:
INSERT INTO room_availability (
    room_id, occupied_from, occupied_to,
    source, source_id
) VALUES (
             #{roomId}, #{occupiedFrom}, #{occupiedTo},
             #{source}, #{sourceId}
         )

mapper.select:
SELECT COUNT(*)
FROM room_availability
WHERE room_id = #{roomId}
AND (
(occupied_from <= #{checkEndDate} AND occupied_to >= #{checkStartDate})
)

mapper.delete:
DELETE FROM room_availability
WHERE source = #{source}
  AND source_id = #{sourceId}
